def toggle_widgets():
    if choice.get() == 1:  # Yes button selected
        com.config(state="disabled")
        take_lati.config(state="normal")
        take_longi.config(state="normal")
    else:  # No button selected
        com.config(state="normal")
        take_lati.config(state="disabled")
        take_longi.config(state="disabled")

def get_data():
    if choice.get() == 1:
        lat = l1.get()
        lon = l2.get()
    else:
        city = city_name.get()
        coord = requests.get("https://geocode.maps.co/search?q=" + city + "&api_key=ENTERYOURKEY").json()
        lat = coord[0]['lat']
        lon = coord[0]['lon']
    data_weather = requests.get(f"https://api.openweathermap.org/data/2.5/weather?lat={lat}&lon={lon}&appid=ENTERYOURKEY").json()
    data_agri = requests.get("https://api.weatherbit.io/v2.0/forecast/agweather?lat=" + str(lat) + "&lon=" + str(lon) + "&key=ENTERYOURKEY").json()
    Weather.config(text = data_weather['weather'][0]['description'])
    Temp11.config(text = str(int(data_weather['main']['temp'] - 273)) + "°C")
    Temp21.config(text = str(data_agri['data'][0]['skin_temp_min']) + "°C")
    Temp22.config(text=str(data_agri['data'][0]['skin_temp_avg']) + "°C")
    Temp23.config(text=str(data_agri['data'][0]['skin_temp_max']) + "°C")
    Wind.config(text=str(data_weather['wind']['speed']) + " m/s")
    Solar.config(text = str(data_agri['data'][0]['dswrf_avg']) + " W/m² · H")
    Evap.config(text = str(data_agri['data'][0]['evapotranspiration']) + " mm")
    Sm1.config(text = str(data_agri['data'][0]['soilm_0_10cm']) + " mm")
    Sm2.config(text = str(data_agri['data'][0]['soilm_10_40cm']) + " mm")
    Sm3.config(text = str(data_agri['data'][0]['soilm_40_100cm']) + " mm")
    Sm4.config(text = str(data_agri['data'][0]['soilm_100_200cm']) + " mm")
    Prec.config(text = str(data_agri['data'][0]['precip']) + " mm")
    Atmp.config(text = str(data_weather['main']['pressure']) + " mBar")
    sr = datetime.datetime.fromtimestamp(data_weather['sys']['sunrise']).isoformat()
    print("sunrise = ", sr[11::1])
    Sunrise.config(text = sr[11::1])
    ss = datetime.datetime.fromtimestamp(data_weather['sys']['sunset']).isoformat()
    print("sunset = ", ss)
    Sunset.config(text = ss[11::1])


